import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar } from '@ionic/react';
import React, { useState } from 'react';
import axios from "axios";
import { IonGrid, IonRow, IonCol } from '@ionic/react';
import { personCircle } from "ionicons/icons";
import { useHistory } from "react-router-dom";
import { IonItem, IonLabel, IonInput, IonButton, IonIcon, IonAlert } from '@ionic/react';


const Home: React.FC = () => {
  const history = useHistory();
  const [validateMobile,setvalidateMobile] = useState<string>("");

  const [addMobile,setaddMobile] = useState<string>("");
  const [addName,setaddName] = useState<string>("");
  const [addAddress,setaddAddress] = useState<string>("");

  const [updateId,setupdateId] = useState<string>("");
  const [updateMobile,setupdateMobile] = useState<string>("");
  const [updateName,setupdateName] = useState<string>("");
  const [updateAddress,setupdateAddress] = useState<string>("");

  const [deleteId,setdeleteId] = useState<string>("");

  const [Allinfo, setInfo] = useState<string>("");

  const [CCode, setCCode] = useState<string>("");

  const [CSign, setCsign] = useState<string>("");

  const [CName, setCName] = useState<string>("");

  const [DeleteRes, setDeleteRes] = useState<string>("");
  const [UpdateRes, setUpateRes] = useState<string>("");
  const [AddRes, setAddRes] = useState<string>("");

  const [iserror, setIserror] = useState<boolean>(false);
  const [message, setMessage] = useState<string>("");

  const handleValidate = () => {
    if (!validateMobile) {
        setMessage("Please enter a mobile number");
        setIserror(true);
        return;
    }

    const validateData = {
        "mobile_number": validateMobile,

    }

    const api = axios.create({
        baseURL: `http://185.82.34.86:8084/Customer/customer/`

    })
    console.log("loginData",validateData)
    api.post("validateMobileNumber", validateData)
        .then(res => { 
          console.log("validateres",res)            
            // history.push("/dashboard/" + email);
         })
         .catch(error=>{
           console.log("error",error)
            setMessage("Error Connection");
            setIserror(true)
         })


  };

 const handleAdd = () => {
      if (!addName || !addAddress ||!addMobile ) {
          setMessage("Please enter info");
          setIserror(true);
          return;
      }

      const AddData = {
        "name":addName,
        "address":addAddress,
        "mobile_number": addMobile,

         }
         const api = axios.create({
          baseURL: `http://185.82.34.86:8084/Customer/customer/`

      })
      console.log("addCustomer",AddData)
      api.post("addCustomer", AddData)
          .then(res => { 
            console.log("addCustomerRes",res)            
              // history.push("/dashboard/" + email);
           })
           .catch(error=>{
             console.log("error",error)
              setMessage("Error Connection");
              setIserror(true)
           })

    }


    const handleUpdate = () => {
      if (!updateId || !updateName || !updateAddress || !updateMobile ) {

          setMessage("Please enter info");
          setIserror(true);
          return;
      }

      const UpdateData = {
        "id":updateId,
        "name":updateName,
        "address":updateAddress,
        "mobile_number": updateMobile,

       } 

       const api = axios.create({
        baseURL: `http://185.82.34.86:8084/Customer/customer/`

    })
    console.log("updateData",UpdateData)
    api.post("update", UpdateData)
        .then(res => { 
          console.log("updateres",res)            
            // history.push("/dashboard/" + email);
         })
         .catch(error=>{
           console.log("error",error)
            setMessage("Error Connection");
            setIserror(true)
         })

    }  

    const handleDelete = () => {
      if (!deleteId) {
          setMessage("Please enter info");
          setIserror(true);
          return;
      }
      const DeleteData = {
        "id": deleteId,

         }
         const api = axios.create({
          baseURL: `http://185.82.34.86:8084/Customer/customer/`

      })
      console.log("deleteById",DeleteData)
      api.post("deleteById", DeleteData)
          .then(res => { 
            console.log("deleteres",res)            
              // history.push("/dashboard/" + email);
           })
           .catch(error=>{
             console.log("error",error)
              setMessage("Error Connection");
              setIserror(true)
           })

    }  
    const handleGetAll = () => {
      const api = axios.create({
        baseURL: `http://185.82.34.86:8084/Customer/customer/`

    })


    console.log("loginData",{})
    api.post("getAllCustomer", {})
        .then(res => { 
          setInfo(JSON.stringify(res["data"]))
          console.log("getAllData",res)            
            // history.push("/dashboard/" + email);
         })
         .catch(error=>{
           console.log("error",error)
            setMessage("Error Connection");
            setIserror(true)
         })
    }  

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Challenge</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen className="ion-padding ion-text-center">
        <IonGrid>
        <IonRow>
          <IonCol>
            <IonAlert
                isOpen={iserror}
                onDidDismiss={() => setIserror(false)}
                cssClass="my-custom-class"
                header={"Error!"}
                message={message}
                buttons={["Dismiss"]}
            />
          </IonCol>
        </IonRow>
        <IonRow>
          <IonCol>
            <IonIcon
                style={{ fontSize: "70px", color: "#0040ff" }}
                icon={personCircle}
            />
          </IonCol>
        </IonRow>
          <IonRow>
            <IonCol>
            <IonItem>
            <IonLabel position="floating"> MobileNumber</IonLabel>
            <IonInput
                type="number"
                value={validateMobile}
                onIonChange={(e) =>setvalidateMobile(e.detail.value!)}
                >
            </IonInput>
            <IonLabel >{CCode}</IonLabel>
            <IonLabel >{CSign}</IonLabel>
            <IonLabel >{CName}</IonLabel>
            </IonItem>
            </IonCol>
          </IonRow>


          <IonRow>
            <IonCol>

              <IonButton expand="block" onClick={handleValidate}>validate</IonButton>


            </IonCol>
          </IonRow>
        </IonGrid>
        <IonGrid>
        <IonRow>
          <IonCol>
            <IonAlert
                isOpen={iserror}
                onDidDismiss={() => setIserror(false)}
                cssClass="my-custom-class"
                header={"Error!"}
                message={message}
                buttons={["Dismiss"]}
            />
          </IonCol>
        </IonRow>
        <IonRow>
          <IonCol>
            <IonIcon
                style={{ fontSize: "70px", color: "#0040ff" }}
                icon={personCircle}
            />
          </IonCol>
        </IonRow>
          <IonRow>
            <IonCol>
            <IonItem>
            <IonLabel position="floating">Mobile</IonLabel>
            <IonInput
                type="number"
                value={addMobile}
                onIonChange={(e) => setaddMobile(e.detail.value!)}
                >
            </IonInput>
            </IonItem>
            </IonCol>
          </IonRow>

          <IonRow>
            <IonCol>
            <IonItem>
              <IonLabel position="floating"> Customer Name</IonLabel>
              <IonInput
                type="text"
                value={addName}
                onIonChange={(e) => setaddName(e.detail.value!)}
                >
              </IonInput>
            </IonItem>
            </IonCol>
          </IonRow>
          <IonRow>
            <IonCol>
            <IonItem>
              <IonLabel position="floating"> Customer Location</IonLabel>
              <IonInput
                type="text"
                value={addAddress}
                onIonChange={(e) => setaddAddress(e.detail.value!)}
                >
              </IonInput>
            </IonItem>
            </IonCol>
          </IonRow>
          <IonRow>
            <IonCol>

              <IonButton expand="block" onClick={handleAdd} >Add</IonButton>


            </IonCol>
            <IonCol>
            <IonLabel >{AddRes}</IonLabel>
            </IonCol>
          </IonRow>
        </IonGrid>
        <IonGrid>
        <IonRow>
          <IonCol>
            <IonAlert
                isOpen={iserror}
                onDidDismiss={() => setIserror(false)}
                cssClass="my-custom-class"
                header={"Error!"}
                message={message}
                buttons={["Dismiss"]}
            />
          </IonCol>
        </IonRow>
        <IonRow>
          <IonCol>
            <IonIcon
                style={{ fontSize: "70px", color: "#0040ff" }}
                icon={personCircle}
            />
          </IonCol>
        </IonRow>
          <IonRow>
            <IonCol>
            <IonItem>
            <IonLabel position="floating"> Cutomer Id</IonLabel>
            <IonInput
                type="number"
                value={updateId}
                onIonChange={(e) => setupdateId(e.detail.value!)}
                >
            </IonInput>
            </IonItem>
            </IonCol>
          </IonRow>

          <IonRow>
            <IonCol>
            <IonItem>
              <IonLabel position="floating">Customer Name </IonLabel>
              <IonInput
                type="text"
                value={updateName}
                onIonChange={(e) => setupdateName(e.detail.value!)}
                >
              </IonInput>
            </IonItem>
            </IonCol>
          </IonRow>
          <IonRow>
            <IonCol>
            <IonItem>
              <IonLabel position="floating">Customer Mobile </IonLabel>
              <IonInput
                type="number"
                value={updateMobile}
                onIonChange={(e) => setupdateMobile(e.detail.value!)}
                >
              </IonInput>
            </IonItem>
            </IonCol>
          </IonRow>
          <IonRow>
            <IonCol>
            <IonItem>
              <IonLabel position="floating">Customer Location </IonLabel>
              <IonInput
                type="text"
                value={updateAddress}
                onIonChange={(e) => setupdateAddress(e.detail.value!)}
                >
              </IonInput>
            </IonItem>
            </IonCol>
          </IonRow>
          <IonRow>
            <IonCol>

              <IonButton expand="block" onClick={handleUpdate}>Update</IonButton>


            </IonCol>
            <IonCol>
            <IonLabel >{UpdateRes}</IonLabel>
            </IonCol>
          </IonRow>
        </IonGrid>
        <IonGrid>
        <IonRow>
          <IonCol>
            <IonAlert
                isOpen={iserror}
                onDidDismiss={() => setIserror(false)}
                cssClass="my-custom-class"
                header={"Error!"}
                message={message}
                buttons={["Dismiss"]}
            />
          </IonCol>
        </IonRow>
        <IonRow>
          <IonCol>
            <IonIcon
                style={{ fontSize: "70px", color: "#0040ff" }}
                icon={personCircle}
            />
          </IonCol>
        </IonRow>
          <IonRow>
            <IonCol>
            <IonItem>
            <IonLabel position="floating"> Id Customer</IonLabel>
            <IonInput
                type="number"
                value={deleteId}
                onIonChange={(e) => setdeleteId(e.detail.value!)}
                >
            </IonInput>
            </IonItem>
            </IonCol>
          </IonRow>


          <IonRow>
            <IonCol>

              <IonButton expand="block" onClick={handleDelete}>Delete</IonButton>


            </IonCol>
          </IonRow>

          <IonRow>
            <IonCol>

              <IonButton expand="block" onClick={handleGetAll}>Get all customer</IonButton>
             <IonLabel >{Allinfo}</IonLabel>

            </IonCol>
          </IonRow>
        </IonGrid>
      </IonContent>
    </IonPage>
  );
};

export default Home;
