import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar } from '@ionic/react';
import React, { useState } from 'react';
import axios from "axios";
import { IonGrid, IonRow, IonCol } from '@ionic/react';
import { personCircle } from "ionicons/icons";
import { useHistory } from "react-router-dom";
import { IonItem, IonLabel, IonInput, IonButton, IonIcon, IonAlert } from '@ionic/react';


const Home: React.FC = () => {
  const history = useHistory();
  const [validateMobile,setvalidateMobile] = useState<string>("");

  const [addMobile,setaddMobile] = useState<string>("");
  const [addName,setaddName] = useState<string>("");
  const [addAddress,setaddAddress] = useState<string>("");


  const [email, setEmail] = useState<string>("");
  const [password, setPassword] = useState<string>("");
  const [iserror, setIserror] = useState<boolean>(false);
  const [message, setMessage] = useState<string>("");

  const handleLogin = () => {
    if (!validateMobile) {
        setMessage("Please enter a mobile number");
        setIserror(true);
        return;
    }




    if (!password || password.length < 6) {
        setMessage("Please enter your password");
        setIserror(true);
        return;
    }

    const validateData = {
        "mobile": validateMobile,

    }
    const UpdateData = {
      "mobile": validateMobile,

     } 
     const DeleteData = {
    "mobile": validateMobile,

     }

     const AddData = {
      "mobile": validateMobile,

       }

    const api = axios.create({
        baseURL: `http://185.82.34.86:8084/Customer/customer/`

    })
    console.log("loginData",validateData)
    api.post("helloWorld", validateData)
        .then(res => { 
          console.log("res",res)            
            // history.push("/dashboard/" + email);
         })
         .catch(error=>{
           console.log("error",error)
            setMessage("Error Connection");
            setIserror(true)
         })
  };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Challenge</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent fullscreen className="ion-padding ion-text-center">
        <IonGrid>
        <IonRow>
          <IonCol>
            <IonAlert
                isOpen={iserror}
                onDidDismiss={() => setIserror(false)}
                cssClass="my-custom-class"
                header={"Error!"}
                message={message}
                buttons={["Dismiss"]}
            />
